mapper.delete:
	update chatdialog set sortno=sortno-1 from (select sortno,userid where id=#{id,jdbcType=VARCHAR}) a where sortno>a.sortno and userid=a.userid;
  delete from chatdialog
  where id = #{id,jdbcType=VARCHAR}

mapper.update:
  	update chatdialog set sortno=0 where id=#{id};
update chatdialog c inner join (select b.sortno,b.userid from chatdialog b where b.id=#{id}) a on 1=1 set c.sortno=c.sortno+1 where c.sortno>a.sortno and c.userid=a.userid ;
update chatdialog set sortno=1 where id=#{id};
